function.body:
CORDERLINE VARCHAR(35);
CORDER VARCHAR(35);
V_BUDGET VARCHAR(35);
SUBTOTAL NUMBER;
TOTAL NUMBER;
BEGIN

	IF TG_OP = 'DELETE' THEN

		CORDER := OLD.C_INVOICE_ID;

		SELECT EM_APM_BUDGET_ID INTO V_BUDGET
		FROM C_INVOICE WHERE C_INVOICE_ID = CORDER;

		IF V_BUDGET IS NOT NULL THEN

			SELECT BUDGET_DOCUMENT INTO TOTAL
			FROM APM_BUDGET WHERE APM_BUDGET_ID = V_BUDGET;

			SUBTOTAL := OLD.LINE_GROSS_AMOUNT;

			TOTAL := TOTAL - SUBTOTAL;

			--RAISE NO_DATA_FOUND||TOTAL;

			UPDATE APM_BUDGET SET BUDGET_DOCUMENT = TOTAL
			WHERE APM_BUDGET_ID = V_BUDGET;

		END IF;
	ELSE
		IF TG_OP = 'INSERT' THEN
		CORDERLINE := NEW.C_INVOICELINE_ID;
		ELSE
		CORDERLINE := OLD.C_INVOICELINE_ID;
		END IF;

		SELECT C_INVOICE_ID,LINE_GROSS_AMOUNT INTO CORDER,SUBTOTAL
		FROM C_INVOICELINE WHERE C_INVOICELINE_ID = CORDERLINE;

		SELECT EM_APM_BUDGET_ID INTO V_BUDGET
		FROM C_INVOICE WHERE C_INVOICE_ID = CORDER;

			IF V_BUDGET IS NOT NULL THEN

			SELECT BUDGET_DOCUMENT INTO TOTAL
			FROM APM_BUDGET WHERE APM_BUDGET_ID = V_BUDGET;

			TOTAL := TOTAL + SUBTOTAL;

			UPDATE APM_BUDGET SET BUDGET_DOCUMENT = TOTAL,C_ORDER_ID = CORDER
			WHERE APM_BUDGET_ID = V_BUDGET;

		END IF;
	END IF;

IF TG_OP = 'UPDATE' THEN RETURN OLD; ELSE RETURN NEW; END IF;
END APM_PAYMENTINVOICE_TRG
